// Helper function to get party color
export const getPartyColor = (party) => {
  if (party.includes('PH')) return '#3B82F6' // Blue for Pakatan Harapan
  if (party.includes('PN')) return '#EF4444' // Red for Perikatan Nasional
  if (party.includes('Independent')) return '#6B7280' // Gray for Independent
  return '#8B5CF6' // Purple for others
}

// Helper function to format party display
export const formatParty = (party) => {
  return party.replace('–', '•').replace('/', '•')
}